package JavaBasics.JavaBasics;

public class loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//while loop
		int i = 1;
		while(i<=10) {
			System.out.println(i);
			i++;
		}
		
		// For loop
		for(int j=1; j<=10; j++) {
			System.out.println("Output for loop "+j);
		}
		
		// print 10 to 1
		for(int k=10; k>=1; k--) {
			System.out.println(k);
		}
	}

}
